select id, name, abscissa, ordinate, note, url_path
from scientific_research.rights
             limit #{offset}, #{limit}

select
id, name, abscissa, ordinate, note, url_path
from scientific_research.rights


        and id = #{id}


        and name = #{name}


        and abscissa = #{abscissa}


        and ordinate = #{ordinate}


        and note = #{note}


        and url_path = #{urlPath}
