mapper.select:
select
id, name, begin_date, paper_type, record_id, descipline, first_discipline, college_id, paper_source, user_id,
information
from scientific_research.paper_achievement


        and id = #{id}


        and name = #{name}


        and begin_date = #{beginDate}


        and paper_type = #{paperType}


        and record_id = #{recordId}


        and descipline = #{descipline}


        and first_discipline = #{firstDiscipline}


        and college_id = #{collegeId}


        and paper_source = #{paperSource}


        and user_id = #{userId}


        and information = #{information}
